function Data() {
    let a = 1
    a = 2
    console.log(a)

    const b = 10
    // b = 20 // Error: Assignment to constant variable.
    console.log(b) // b = 10

    const name = '겨울이'
    const age = 2
    const visible = false
    const user = {
        name: '가을이',
        age: 1,
    }
    const array = [1, 2, 3]

    const users = [
        { name: '여름이', age: 1 },
        { name: '가을이', age: 5 },
        { name: '겨울이', age: 2 },
    ]

    const info = {
        birth: '2020-01-01',
        phone: null,
    }

    console.log(info.birth) // 2020-01-01
    console.log(info.phone) // null
    console.log(info.address)

    return (
        <>
            <ul>
                {users.map((user, index) => (
                    <li key={index}>
                        {user.name} 나이는 {user.age}살 입니다.
                    </li>
                ))}
            </ul>
            <ul>
                <li>
                    {users[0].name} 나이는 {users[0].age}살 입니다.
                </li>
                <li>
                    {users[1].name} 나이는 {users[1].age}살 입니다.
                </li>
                <li>
                    {users[2].name} 나이는 {users[2].age}살 입니다.
                </li>
            </ul>
            <ul>
                {array.map((item) => (
                    <li>{item}</li>
                ))}
            </ul>
            <ul>
                <li>{array[0]}</li>
                <li>{array[1]}</li>
                <li>{array[2]}</li>
            </ul>
            <p>나의 이름은 {name}입니다.</p>
            <p>나의 나이는 {age}살입니다.</p>
            {/* {조건 ? 참 : 거짓} */}
            {visible ? <p>로그인</p> : <p>로그아웃</p>}
            <p>
                나의 이름은 {user.name}이고, 나이는 {user.age}살이야
            </p>

            <Func1 />
            <Func2 />
            <Func3 />
        </>
    )
}

// 함수 선언식 (기명 함수)
function Func1() {
    return <p>기명 함수</p>
}

// 함수 표현식 (익명 함수)
const Func2 = function () {
    return <p>익명 함수</p>
}

// 화살표 함수
const Func3 = () => {
    return <p>화살표 함수</p>
}

export default Data
